00_sample-and-time:
// play different samples
// at different time intervals

set tempo 95

new sample bamboo_a time(1)
new sample bamboo_c time(1/2)
new sample bamboo_f time(1/3)
new sample bamboo_g time(1/4)

// add reverb fx to all samples
set all fx(reverb 0.8 5)

01_offset-in-time:
// multiple samples make up a beat
// using a time offset as 
// second argument

set tempo 131

new sample kick_909 time(1/4)
new sample snare_909 time(1/2 1/4)
new sample hat_909 time(1/4 1/8)
new sample hat_909_open time(1 15/16)
new sample clap_909 time(5/16)

// short slapback reverb
set all fx(reverb 1 3)

02_linear-beat:
// choose 15 random samples from a list of
// predefined samplename. Play those samples 
// sequentially for every time interval

set tempo 112

// randomseed makes sure every run the 
// random values are the same
set randomSeed 1234

list samples [tabla_lo tabla_hi tabla_mid tabla_hi_short]
list selection choose(15 samples)

new sample selection time(1/16)
new sample kick_house time(1/4)

set all fx(reverb 0.3 3)

03_sample-scrubbing:
// make 2 different position lists of 16 values 
// from a randomFloat() method and combine them
// into one list. Fade-in the sample in 1 ms, 
// Play it for 50 ms and fade-out 2 in ms with shape()

set tempo 131

// randomseed makes sure every run the 
// random values are the same
// try some different values to hear the result
set randomSeed 3141

list pos1 randomFloat(16 0 0.2)
list pos2 randomFloat(16 0.2 0.3)
list play join(pos1 pos1 pos1 pos2)

new sample harp_up time(1/16) shape(1 100) offset(play) gain(2)

set all fx(reverb 0.5 11)

04_possibility-rhythm:
// make a rhythm with ones and zeroes
// in a list. A value between one and zero is
// a probability (for example 0.5 = 50% chance)

set tempo 83

new sample bamboo_a time(1/4)

// try changing the values in the list
// try adding/removing values as well
list rhythm [1 0 0 1 0.1 0.5 0.2]
new sample bamboo_f time(1/16) beat(rhythm)

set all fx(reverb 0.4 3)

05_synth-and-note:
// play a melody from a list of semitones
// use different octaves with the second
// argument in the note() method

set tempo 98

// star wars melody as relative intervals
list jedi [-5 0 2 3 5 3 -5 -5 0 2 3 -5 3 0 7 5]

new synth sine name(luke)
    set luke note(jedi 2) shape(5 300) time(1/6)

new synth sine name(leia)
    set leia note(jedi 3) shape(5 90) time(1/8)

// apply a reverb, distortion and distortion to all synths
set all fx(reverb 0.3 9) fx(drive 5) fx(delay 1/12 0.3)

06_generative-melody:
// generate a small theme from a sine function 
// and transform it with various methods like
// invert(), join() and clone()

set tempo 98

list theme sine(16 5 0 24)
list variation invert(theme 0 24)
list phrase join(theme variation)
list section clone(phrase 0 5 9 7)

new synth triangle name(arpy)
    set arpy time(1/16) shape(1 170) note(section 1)

    // give the synth random panning, a filter and a delay
    set arpy pan(random) fx(filter low 2300 0.4) fx(delay 5/16 0.5)

07_granular-playback:
// simple granular timestretching is achieved
// by quickly playing short grains of a sample
// and changing the offset of the playback

set tempo 120

// a position ramp up-down over 2000 values
list pos sineFloat(1100 1 0 0.5)

// small detuning for every sample playback
list detune randomFloat(1000 0.95 1.05)

new sample bowl_mid name(grain)
    set grain time(1/32) shape(4 50 4) 
    set grain offset(pos) pan(random)
    set grain fx(delay 2/32 0.7)
    set grain speed(detune)

08_modulating-fx:
// combine multiple effects modulated in time by lists
// to create interesting synthesized sounds

set tempo 132

new synth saw note([0 1 [12 24 19 36]] 0) name(syn)
	// modulate the length of the note with a cosine function
	set syn shape(1 cosine(128 3.14 50 300)) time(1/16) 
    // randomnes to the slide between notes and the overdrive fx
    set syn slide([[100 0] 0 0]) fx(drive random(4 2 15)) 
    // slowly rise the filter cutoff frequency
    set syn fx(filter low spread(32 200 3500) 0.6)
    // change delaytimes every 16 notes
    set syn fx(delay repeat([1/32 3/16 7/16] 16) 0.5)

09_lofi-hiphop:
// A lo-fi beat with amenbreak chops and a pitchshifted 
// rhodes sample on the offbeat

set tempo 87
set randomSeed 3210

// chopped amen break
new loop amen time(1/16) start(choose(16 spreadFloat(16)))

// hihats and a kick-hat-snare-hat beat
new sample hat_808 time(1/16) pan(random) gain(0.5)
new sample [kick_dub hat_808_open snare_rock hat_808_open] time(1/4)

new sample rhodes_8bit name(rhodes)
	set rhodes time(1/4 1/8) fx(shift repeat([0 3 7 2] 8))
    set rhodes shape(1 1/8 1) fx(reverb 0.3 7)

11_808-beat:
// 808 beats
// change the tempo
set tempo 115
// try different numbers
set randomSeed 9832

// various sounds in a list
list manySounds [kick_808 hat_808 hat_808_semi maracas_808 snare_808 tom_808]
// choose 16 names randomly from the list
list pattern choose(16 manySounds)
// play the sounds with a squash effect and random panning on left/right speaker
new sample pattern time(1/16) fx(squash 5) pan(random)

12_drone-synths:
// A synth that functions as bass drone 
// Combined with a short high pitch synth with delay
// Creating in a melody from a lookup scale list

set tempo 124
// define a scale
list minor_harmonic [0 0 2 3 3 5 7 7 8 8 11 11]

// some note numbers for bass synth
list notes [3 7 5 8]
new synth saw note(notes 0) time(4) name(drone)
	set drone fat(0.1132 3) shape(off) fx(squash 5)
    set drone fx(reverb 0.2 3) fx(filter low 1500 0.4)

// create a melody for the lead synth
list melody lookup(cosine(16 5.32 0 12) minor_harmonic)
new synth sine note(melody 2) time(1/2) name(lead)
	set lead shape(1 120) fat(0.021 3) fx(distort 10)
    set lead fx(delay 3/16 0.7) fx(reverb 0.5 3)
